select.execute:
url = request.url;

var myReq = y.rest(url);

//get plain text back from OPI endpoint
rawStatus = myReq.get().response;

//check if users is not offline
if (!rawStatus.match("NOT ONLINE")) {
    status = "online";
} else {
    status = "offline";
}

//return results as XML
response.object = 
<messengerstatus>
   <yahoo_id>{u}</yahoo_id>
   <status>{status}</status>
</messengerstatus>
